"use strict";

document.addEventListener("DOMContentLoaded", function () {
  var e = document.getElementById("acercade").offsetTop,
      f = document.getElementById("galeria").offsetTop,
      b = document.getElementById("menu").offsetTop,
      c = document.getElementById("ubicacion").offsetTop;
  document.getElementById("btn-acercade").addEventListener("click", function (a) {
    a.preventDefault(), window.scroll({
      behavior: "smooth",
      left: 0,
      top: e
    });
  }), document.getElementById("btn-galeria").addEventListener("click", function (b) {
    b.preventDefault(), window.scroll({
      behavior: "smooth",
      left: 0,
      top: f
    });
  }), document.getElementById("btn-menu").addEventListener("click", function (c) {
    c.preventDefault(), window.scroll({
      behavior: "smooth",
      left: 0,
      top: b + 550
    });
  }), document.getElementById("btn-ubicacion").addEventListener("click", function (b) {
    b.preventDefault(), window.scroll({
      behavior: "smooth",
      left: 0,
      top: c
    });
  });
});