document.addEventListener("DOMContentLoaded",function(){var a=document.getElementById("acercade").offsetTop,b=document.getElementById("galeria").offsetTop,c=document.getElementById("menu").offsetTop,d=document.getElementById("ubicacion").offsetTop;document.getElementById("btn-acercade").addEventListener("click",b=>{b.preventDefault(),window.scroll({behavior:"smooth",left:0,top:a})}),document.getElementById("btn-galeria").addEventListener("click",a=>{a.preventDefault(),window.scroll({behavior:"smooth",left:0,top:b})}),document.getElementById("btn-menu").addEventListener("click",a=>{a.preventDefault(),window.scroll({behavior:"smooth",left:0,top:c+550})}),document.getElementById("btn-ubicacion").addEventListener("click",a=>{a.preventDefault(),window.scroll({behavior:"smooth",left:0,top:d})})});